const apiKey = "b7RJuDNmlM9_qAo_rkKRHEtPsIzf3h4lebmp41B5cfXmBHQmqc0qgwwtOJf8vo2bqYHFGbK5dRXDRqtXejS3t-cxf9rIDC8j-kvWkyNuV7zwMuj5nKe1g3tEKSrvZXYx";

const Yelp = {
    search(searchTerm, location, sortBy) {
        const apiKey = process.env.YELP_API_KEY;

        if (!apiKey) {
            throw new Error('YELP_API_KEY environment variable is missing.');
        }

        return fetch(
            `https://cors-anywhere.herokuapp.com/https://api.yelp.com/v3/businesses/search?term=${searchTerm}&location=${location}&sort_by=${sortBy}`,
            {
                headers: {
                    Authorization: `Bearer ${apiKey}`
                }
            }
        )
        .then((response) => {
            if (!response.ok) {
                throw new Error('Network response was not ok.');
            }
            return response.json();
        })
        .then((jsonResponse) => {
            if (jsonResponse.businesses) {
                return jsonResponse.businesses.map((business) => ({
                    id: business.id,
                    imageSrc: business.image_url,
                    name: business.name,
                    address: business.location.address1,
                    city: business.location.city,
                    state: business.location.state,
                    zipCode: business.location.zip_code,
                    category: business.categories[0].title,
                    rating: business.rating,
                    reviewCount: business.review_count
                }));
            } else {
                throw new Error('Unexpected response from Yelp API.');
            }
        })
        .catch((error) => {
            console.error('Error fetching data from Yelp API:', error);
            throw error; // Rethrow the error to be caught by the caller
        });
    }
};

export default Yelp;
